var checkSuggestions  = [];
  function tagCloseFunc(a) {
    a.parentElement.style.display = "none";
  }
  function addtag(a) {
    
    if ("BUTTON" == a.tagName) 
    {
      var e = document.getElementById("myInput");
      var i = document.getElementById("utils_id");
      
      if ((e.value.length > 0) && (i.value.length > 0)) 
      {
        if(!checkSuggestions.includes(e.value))
        {
          var n = `<div class="chip "> 
          <div class="tag-text">\n  ${e.value}\n </div> <input type="hidden" class="tag-id" value="${i.value}" name="tag_id[]"/> <img class="closebtn "  onclick="tagCloseFunc(this)" src="form_assets/img/Vector.png ">\n   
          <div></div>
          </div>`,
          t = document.getElementById("save-tag").innerHTML;
        (t += n), (document.getElementById("save-tag").innerHTML = t);
        checkSuggestions.push(e.value);
        }
      
      }
    } else if ("IMG" == a.tagName) 
    {
      n = `<div class="chip ">
        <div class="tag-text">\n${(t = (t = (t = a.parentNode).outerText).slice(0,-1))}\n</div>
        <img class="closebtn "  onclick="tagCloseFunc(this)" src="form_assets/img/Vector.png">\n  </div>`;
      var i = document.getElementById("save-tag").innerHTML;
      (i += n), (document.getElementById("save-tag").innerHTML = i);
    }
  }

  $("#sidebarToggle").click(function(){
    if($("#sidebarToggle > i").hasClass("fa-angle-left"))
    {
      $("#sidebarToggle > i").removeClass('fa-angle-left');
      $("#sidebarToggle > i").addClass('fa-angle-right');
    }else
    {
      $("#sidebarToggle > i").removeClass('fa-angle-right');
      $("#sidebarToggle > i").addClass('fa-angle-left');
    }

  });